%Witek Aleksandra lista6 zad2clear all;%do rysowania okregualfa = linspace(0,2*pi);%srodek okregu w punkcie (0,0)x0=0;y0=0;%przykladowe wartosci z zadania zamienione na metrylambda = 5 * 10^(-7); R = 2;%przykładowa liczba promieni n = 7;for n=1:n  a = sqrt(R*lambda*n); %promien okregur=2*a;%przeksztalcone rownanie okregux = r*cos(alfa)+x0; y = r*sin(alfa)+y0; plot(x,y)hold on;%https://www.fuw.edu.pl/wo/data/prac-fiz-p-newtona.pdfa1=((lambda*R)*(n-(1/2)))^(1/2);r1=2*a1;x1 = r1*cos(alfa)+x0; y1 = r1*sin(alfa)+y0; title ('Rozkład natężenia pierścieni Newtona w zależności od środka soczewki r')xlabel('x[m]')ylabel('y[m]')%wyrownanie osiaxis square;endhold off;